mapper.select:
SELECT `id`, `store_id`, `user_id`, `order_sn`, `order_status`, `consignee`, `mobile`, `address`, `message`, `goods_price`, `freight_price`, `coupon_price`, `integral_price`, `groupon_price`, `order_price`, `actual_price`, `pay_id`, `pay_time`, `ship_sn`, `ship_channel`, `ship_time`, `confirm_time`, `comments`, `end_time`, `add_time`, `update_time`, `deleted` FROM lite_mall_order
`store_id` = #{storeId} and `user_id` = #{userId} and `order_sn` = #{orderSn}
and `order_status` in
#{status}
and ${sql_filter}
ORDER BY add_time DESC

mapper.update:
UPDATE lite_mall_order
`store_id` = #{storeId}, `user_id` = #{userId}, `order_sn` = #{orderSn}, `order_status` = #{orderStatus}, `consignee` = #{consignee}, `mobile` = #{mobile}, `address` = #{address}, `message` = #{message}, `goods_price` = #{goodsPrice}, `freight_price` = #{freightPrice}, `coupon_price` = #{couponPrice}, `integral_price` = #{integralPrice}, `groupon_price` = #{grouponPrice}, `order_price` = #{orderPrice}, `actual_price` = #{actualPrice}, `pay_id` = #{payId}, `pay_time` = #{payTime}, `ship_sn` = #{shipSn}, `ship_channel` = #{shipChannel}, `ship_time` = #{shipTime}, `confirm_time` = #{confirmTime}, `comments` = #{comments}, `end_time` = #{endTime}, `add_time` = #{addTime}, `update_time` = #{updateTime}, `deleted` = #{deleted}
`id` = #{id} and `store_id` = #{storeId} and `user_id` = #{userId} and `order_status` = #{orderStatus} and `deleted` = #{deleted}

mapper.select:
SELECT
	COUNT(1)
FROM
lite_mall_order

	 `store_id` = #{storeId}
	 and `user_id` = #{userId}
	 and `order_sn` = #{orderSn}
	 and `order_status` = #{orderStatus}
