mapper.select:
SELECT m.menu_id, m.parent_id, ( SELECT p.name FROM sys_menu p WHERE p.menu_id = m.parent_id ) AS parentName, m.name, m.code, m.url, m.perms, m.type, m.icon, m.order_num FROM sys_menu m
and ${sql_filter}
ORDER BY m.order_num ASC

mapper.update:
UPDATE sys_menu
parent_id = #{parentId}, name = #{name}, url = #{url}, perms = #{perms}, type = #{type}, icon = #{icon}, order_num = #{orderNum}, update_time = NOW()
where menu_id = #{menuId}
